import React, { Component } from 'react';
import { connect } from "react-redux";
import fetchUsers from '../../store/actions/fetchUsers/fetchUsers';

import Spinner from '../../components/UI/Spinner/Spinner';
import Discovery from '../../components/Discovery/Discovery';
import './DiscoveryList.css';

class DiscoveryList extends Component {
  componentDidMount() {
    this.props.fetchUsers()
  }
  render() {
    console.log(this.props.users, 'users')
    console.log(this.props.courses, 'courses')
    const { users, spinner, courses } = this.props
    let context = spinner ? <Spinner /> : courses.map(course => {
      console.log(course, 'adsdasdasadsknadsdajkbsdkj')
      return course.map(item => (
        <Discovery
          key={item.id}
          img={item.avatar}
          // title={user.first_name}
          // subtitle={course.name}
        />
      ))

    })

    return (
      <div className="disoverListWrraper">
        {context}
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state, 'dsadasadsadsadsads')
  return {
    users: state.users.users,
    spinner: state.spinner.spinner,
    courses: state.courses.courses
  }
};

const mapDispatchToProps = dispatch => {
  return {
    fetchUsers: () => dispatch(fetchUsers())
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(DiscoveryList);
